                 x= "GD", 
                 y = "Pts",
                 trendline='ols',
                 labels = dict(GD = "Goal Differential", Pts = "Points"),
                 template = 'simple_white',
                 title = "NWSL Goals and Results, 2021",
                 text = 'Abbr'
                 )

fig = fig.update_traces(textposition = 'top center')      

fig.show()
